import React from "react";
import {
  View,
  SafeAreaView,
  Image,
  Text,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import Icon from "react-native-vector-icons/MaterialIcons";

const DetailsScreen = ({ navigation, route }) => {
  return (
    <View>
      <Text
        // Gọi để dùng css
        style={styles.text}
      >
        {/* Khoang cach giua cac chu */}
        This is detail{" "}
        <Text
          style={{
            fontStyle: "italic",
            textDecorationLine: "underline",
            color: "#2980b9",
          }}
        >
          product
        </Text>
      </Text>
      <View>
        <View>
          <Image
            source={require("../assets/nuoc_c2.png")}
            style={styles.itemImage}
          />
        </View>
      </View>

      <View>
        <Text style={styles.itemName}>Nước ngọt C2</Text>
        <Text style={styles.itemPrice}>20.000 đ</Text>
        <Text style={styles.shopAddress}>
          52 Bế Văn Đàn, An Bình, Dĩ An, Bình Dương
        </Text>
      </View>

      <View>
        <TouchableOpacity style={styles.btnBuy}>
          <Text style={styles.btnBuyText}>Chọn Mua</Text>
        </TouchableOpacity>
      </View>

      <View>
        <View>
          <Text>Sản phẩm cùng cửa hàng</Text>
        </View>

        <View style={styles.shopForItem}>
          <View>
            <Image
              source={require("../assets/nuoc_c2.png")}
              style={styles.shopImage}
            />
          </View>

          <View>
            <Text style={styles.shopName}>Tea 1996</Text>
            <Text style={styles.shopStatus}>20 sản phẩm</Text>
            <Text style={styles.shopAddress}>
              52 Bế Văn Đàn, An Bình, Dĩ An, Bình Dương
            </Text>
          </View>

          <View>
            <TouchableOpacity style={styles.btnShop}>
              <Text style={styles.btnShopText}>Xem cửa hàng</Text>
            </TouchableOpacity>
          </View>
        </View>
        {/* San pham lien quan */}
        <View style={styles.shopForItem}>
          <View >
            <Image
              source={require("../assets/nuoc_c2.png")}
              style={styles.itemImage}
            />
            <Text style={styles.itemPrice}>20.000 đ</Text>
          </View>

          <View>
            <Image
              source={require("../assets/nuoc_c2.png")}
              style={styles.itemImage}
            />
            <Text style={styles.itemPrice}>20.000 đ</Text>
          </View>

          <View>
            <Image
              source={require("../assets/nuoc_c2.png")}
              style={styles.itemImage}
            />
            <Text style={styles.itemPrice}>20.000 đ</Text>
          </View>

        
        </View>
      </View>
    </View>
  );
};

const TEXTLIGHT = {
  color: "#fff",
  textAlign: "center",
};

const TEXTDARK = {
  color: "#000",
  fontWeight: "bold",
};

// Tạo biến chứa và css
const styles = StyleSheet.create({
  text: {
    marginTop: 50,
    textAlign: "center",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
  },

  brnBuyText: {
    ...TEXTLIGHT,
    justifyContent: "center",
  },

  btnBuy: {
    height: 50,
    justifyContent: "center",
    alignItems: "center",
    backgroundColor: "#E94730",
    borderRadius: 25,
    margin: 30,
  },

  itemName: {
    ...TEXTDARK,
  },

  itemPrice: {
    ...TEXTDARK
  },

  shopName: {
    ...TEXTDARK,
  },

  // Dồn thành 1 hàng
  shopForItem: {
    flexDirection: "row",
    justifyContent: "space-between",
  },
});

export default DetailsScreen;
