import React from "react";
import { View, Text, Image } from "react-native";

const ProductDetail = () => {
  return (
    <View>
      <View style={{ flex: 1, alignItems: "center", justifyContent: "center" }}>
        <Text>This is Product Detail Screen</Text>
      </View>

      
      <View>
        {/* <Image source={require("../assets/nuoc_c2.png")} /> */}
        <Image
          source={{ uri: "https://freetuts.net/public/logo/logo.png" }}
          style={{ width: 318, height: 40 }}
        />
      </View>

      <View>
        <Text>Nước ngọt c2</Text>
        <Text>20.000 đ</Text>
        <Text>Địa chỉ cửa hàng</Text>
      </View>

      <View>
        <Button>Chọn Mua</Button>
      </View>
      
      <View>
        <Text>Sản phẩm cùng cửa hàng</Text>
        <View>
          {/* <Image source={require("../assets/nuoc_c2.png")}></Image> */}
          <Text>Tên cửa hàng</Text>
          <Text>20 còn lại</Text>
          <Text>Địa chỉ cửa hàng</Text>
        </View>
        <View>
          <Button>Chọn Mua</Button>
        </View>
        <View>
            <Text>Sản phẩm liên quan</Text>
        </View>
      </View>


    </View>
  );
};

export default ProductDetail;
