import React from "react";
import {
  View,
  SafeAreaView,
  Image,
  Text,
  StyleSheet,
  TouchableOpacity,
} from "react-native";
import Icon from "react-native-vector-icons/MaterialIcons";

const DetailsScreen = ({ navigation, route }) => {
  return (
    <View>
      <Text
        // Gọi để dùng css
        style={styles.text}
      >
        {/* Khoang cach giua cac chu */}
        This is detail{" "}
        <Text
          style={{
            fontStyle: "italic",
            textDecorationLine: "underline",
            color: "#2980b9",
          }}
        >
          product
        </Text>
      </Text>

      <View>
        <TouchableOpacity style={styles.btnBuy}>
          <Text style={styles.btnBuyText} >Chọn Mua</Text>
        </TouchableOpacity>
      </View>
    </View>
  );
};

const TEXTDARK = {
  color: '#000',
  textAlign: 'center',
  flex: 1, 
  justifyContent: 'center'
}

const TEXTLIGHT = {
  color: '#fff',
  textAlign: 'center',
  flex: 1, 
  justifyContent: 'center'
}

// Tạo biến chứa và css
const styles = StyleSheet.create({
  text: {
    ...TEXTDARK,
    marginTop: 50,
    textAlign: "center",
    fontSize: 30,
    fontWeight: "bold",
    textTransform: "uppercase",
  },

  brnBuyText: {
    ...TEXTLIGHT
  }, 
  
  btnBuy: {
    height: 50,
    justifyContent: 'center',
    alignItems: 'center',
    backgroundColor: '@E94730',
    borderRadius: 25,
    margin: 30,
    
  }
});

export default DetailsScreen;
